tables.table_schema:
CREATE TABLE TournamentDates (
  date_id INT PRIMARY KEY,
  tournament_id INT,
  scheduled_date DATE,
  CONSTRAINT fk_tournament FOREIGN KEY (tournament_id) REFERENCES TournamentSchedule(tournament_id)
);

CREATE TABLE TournamentLocations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255) NOT NULL,
  city VARCHAR(100),
  state VARCHAR(100)
);

CREATE TABLE Team (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(255),
  captain_id INT
);

CREATE TABLE Venue (
  venue_id INT PRIMARY KEY,
  venue_name VARCHAR(255),
  location VARCHAR(255),
  capacity INT
);

CREATE TABLE team_details (
    team_id INT PRIMARY KEY,
    team_name TEXT,
    coach_name TEXT,
    captain_name TEXT
);
