tables.table_schema:
CREATE TABLE practice_sessions (
  session_id INT PRIMARY KEY,
  session_date DATE,
  duration_hours INT,
  coach_name VARCHAR(255)
);

CREATE TABLE practice_goals (
  goal_id INT PRIMARY KEY,
  goal_description TEXT,
  session_id INT,
  FOREIGN KEY (session_id) REFERENCES practice_sessions(session_id)
);

CREATE TABLE practice_sessions (
    session_id UUID PRIMARY KEY,
    date DATE,
    player_id UUID
);

CREATE TABLE drills (
    drill_id UUID PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    duration INT
);

CREATE TABLE goals (
    goal_id UUID PRIMARY KEY,
    player_id UUID,
    description TEXT
);

CREATE TABLE player_stats (
    player_id UUID PRIMARY KEY,
    total_goals INT,
    total_assists INT,
    total_matches INT
);

CREATE TABLE practice_sessions (
   session_id INT PRIMARY KEY,
   session_date DATE,
   session_notes TEXT
);

CREATE TABLE drills (
   drill_id INT PRIMARY KEY,
   drill_name VARCHAR(255),
   drill_description TEXT
);

CREATE TABLE goals (
   goal_id INT PRIMARY KEY,
   goal_description TEXT,
   goal_achieved BOOLEAN
);

CREATE TABLE players (
   player_id INT PRIMARY KEY,
   player_name VARCHAR(255),
   player_position VARCHAR(50)
);
