tables.table_schema:
CREATE TABLE Court_Reservations (
    reservation_id INT PRIMARY KEY,
    court_number INT NOT NULL,
    reservation_date DATE NOT NULL,
    start_time TIME NOT NULL,
    end_time TIME NOT NULL
);

CREATE TABLE Court_Availability (
    court_number INT PRIMARY KEY,
    available_date DATE NOT NULL,
    is_available BOOLEAN NOT NULL
);

CREATE TABLE court_availability (
  court_id INT PRIMARY KEY,
  date DATE,
  available_hours INT,
  max_capacity INT
);

CREATE TABLE CourtAvailability (
    court_id UUID PRIMARY KEY,
    available_date DATE,
    available_start_time TIME,
    available_end_time TIME
);

CREATE TABLE FacilityOrganization (
    organization_id UUID PRIMARY KEY,
    organization_name VARCHAR(255),
    address TEXT
);

CREATE TABLE court_availability (
    court_number INT PRIMARY KEY,
    available BOOLEAN NOT NULL
);

CREATE TABLE court_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(255) NOT NULL,
    phone_number VARCHAR(20)
);
