tables.table_schema:
CREATE TABLE sponsorship_details (
    sponsor_id INT PRIMARY KEY,
    sponsor_name VARCHAR(50),
    sponsorship_amount DECIMAL(10,2)
);

CREATE TABLE agreements (
    id UUID PRIMARY KEY,
    agreement_number VARCHAR(50) NOT NULL,
    contract_id UUID NOT NULL REFERENCES contracts(id),
    details TEXT
);

CREATE TABLE SponsorshipDetails (
    id INT PRIMARY KEY,
    sponsor_name TEXT,
    sponsor_contact TEXT,
    sponsorship_amount DECIMAL
);

CREATE TABLE Contracts (
    contract_id INT PRIMARY KEY,
    sponsor_id INT,
    contract_start_date DATE,
    contract_end_date DATE
);

CREATE TABLE Agreements (
    agreement_id INT PRIMARY KEY,
    contract_id INT,
    agreement_type TEXT,
    agreement_terms TEXT
);

CREATE TABLE FinancialDetails (
    finance_id INT PRIMARY KEY,
    sponsor_id INT,
    finance_date DATE,
    finance_amount DECIMAL
);

CREATE TABLE MarketingDetails (
    marketing_id INT PRIMARY KEY,
    sponsor_id INT,
    marketing_strategy TEXT,
    marketing_channels TEXT
);
