tables.table_schema:
CREATE TABLE Coaches (
  coach_id INT PRIMARY KEY,
  coach_name VARCHAR(255) NOT NULL,
  coaching_specialty VARCHAR(100) NOT NULL
);

CREATE TABLE Coaches (
    coach_id UUID PRIMARY KEY,
    coach_name VARCHAR(255) NOT NULL,
    coaching_specialty VARCHAR(255) NOT NULL
);

CREATE TABLE TrainingPlans (
    plan_id UUID PRIMARY KEY,
    plan_name VARCHAR(255) NOT NULL,
    coach_id UUID REFERENCES Coaches(coach_id),
    plan_description TEXT
);

CREATE TABLE WorkoutLogs (
    log_id UUID PRIMARY KEY,
    log_date DATE NOT NULL,
    coach_id UUID REFERENCES Coaches(coach_id),
    plan_id UUID REFERENCES TrainingPlans(plan_id),
    log_details TEXT
);

CREATE TABLE Clients (
    client_id UUID PRIMARY KEY,
    client_name VARCHAR(255) NOT NULL,
    coach_id UUID REFERENCES Coaches(coach_id)
);

CREATE TABLE Coaches (
  coach_id INT PRIMARY KEY,
  coach_name VARCHAR(50)
);

CREATE TABLE TrainingPlans (
  plan_id INT PRIMARY KEY,
  plan_name VARCHAR(50),
  coach_id INT REFERENCES Coaches(coach_id)
);

CREATE TABLE WorkoutLogs (
  log_id INT PRIMARY KEY,
  log_datetime TIMESTAMP,
  coach_id INT REFERENCES Coaches(coach_id),
  plan_id INT REFERENCES TrainingPlans(plan_id)
);

CREATE TABLE Exercises (
  exercise_id INT PRIMARY KEY,
  exercise_name VARCHAR(50),
  coach_id INT REFERENCES Coaches(coach_id)
);
