tables.table_schema:
CREATE TABLE swimming_pool ( 
 pool_id INT PRIMARY KEY, 
 location VARCHAR(50), 
 capacity INT 
 );

CREATE TABLE pool_equipment ( 
 equipment_id INT PRIMARY KEY, 
 equipment_name VARCHAR(50), 
 quantity INT, 
 pool_id INT REFERENCES swimming_pool(pool_id) 
 );

CREATE TABLE swimming_pool (
    pool_id UUID PRIMARY KEY,
    pool_name VARCHAR(255),
    location VARCHAR(255),
    capacity INT
);
CREATE TABLE equipment (
    equipment_id UUID PRIMARY KEY,
    equipment_name VARCHAR(255),
    quantity INT,
    location VARCHAR(255),
    is_available BOOLEAN
);
CREATE TABLE maintenance_history (
    maintenance_id UUID PRIMARY KEY,
    equipment_id UUID REFERENCES equipment(equipment_id),
    description VARCHAR(255),
    maintenance_date DATE
);

CREATE TABLE equipment_inventory (
  equipment_id INT PRIMARY KEY,
  equipment_name VARCHAR(255) NOT NULL,
  quantity INT,
  price DECIMAL(10, 2)
);

CREATE TABLE pool_facilities (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL,
  capacity INT
);

CREATE TABLE maintenance_logs (
  log_id INT PRIMARY KEY,
  equipment_id INT,
  maintenance_date DATE,
  maintenance_description TEXT
);

CREATE TABLE staff_members (
  staff_id INT PRIMARY KEY,
  staff_name VARCHAR(255) NOT NULL,
  role VARCHAR(50) NOT NULL,
  department VARCHAR(50)
);
