CREATE TABLE golf_courses (
	course_id INT PRIMARY KEY,
	course_name VARCHAR(255) NOT NULL,
	location VARCHAR(255) NOT NULL
);

CREATE TABLE course_layouts (
	layout_id INT PRIMARY KEY,
	course_id INT REFERENCES golf_courses(course_id),
	par INT NOT NULL,
	hole_number INT NOT NULL
);

CREATE TABLE holes (
	hole_id INT PRIMARY KEY,
	layout_id INT REFERENCES course_layouts(layout_id),
	par INT NOT NULL,
	yardage INT NOT NULL
);

CREATE TABLE golfers (
	golfer_id INT PRIMARY KEY,
	first_name VARCHAR(255) NOT NULL,
	last_name VARCHAR(255) NOT NULL
);

CREATE TABLE scores (
	score_id INT PRIMARY KEY,
	golfer_id INT REFERENCES golfers(golfer_id),
	hole_id INT REFERENCES holes(hole_id),
	score INT NOT NULL
);
