tables.table_schema:
CREATE TABLE maintenance_schedule (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(50) NOT NULL,
    last_maintenance_date DATE,
    next_maintenance_date DATE
);

CREATE TABLE mower_schedule (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    last_maintenance_date DATE,
    next_maintenance_date DATE
);

CREATE TABLE tractor_schedule (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    last_maintenance_date DATE,
    next_maintenance_date DATE
);

CREATE TABLE equipment_maintenance_schedule (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(255),
    last_maintenance_date DATE,
    next_maintenance_date DATE
);

CREATE TABLE mower_details (
    mower_id INT PRIMARY KEY,
    mower_type VARCHAR(255),
    manufacturer VARCHAR(255),
    purchase_date DATE
);

CREATE TABLE irrigation_system (
    system_id INT PRIMARY KEY,
    system_type VARCHAR(255),
    location VARCHAR(255),
    install_date DATE
);

CREATE TABLE Mowers (
    id INT PRIMARY KEY,
    brand VARCHAR(255),
    model VARCHAR(255),
    last_maintenance_date DATE
);

CREATE TABLE Tractors (
    id INT PRIMARY KEY,
    brand VARCHAR(255),
    model VARCHAR(255),
    last_maintenance_date DATE
);

CREATE TABLE IrrigationSystems (
    id INT PRIMARY KEY,
    brand VARCHAR(255),
    model VARCHAR(255),
    last_maintenance_date DATE
);
