tables.table_schema:
CREATE TABLE golf_event_registrations (
	participant_id INT,
	participant_name VARCHAR(50),
	event_date DATE,
	registration_fee DECIMAL
);

CREATE TABLE participant_details (
  participant_id INT PRIMARY KEY,
  participant_name VARCHAR(100),
  participant_email VARCHAR(100) UNIQUE
);

CREATE TABLE event_details (
  event_id INT PRIMARY KEY,
  event_name VARCHAR(100),
  event_date DATE,
  registration_fee DECIMAL(10, 2)
);

CREATE TABLE participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(50),
    participant_email VARCHAR(100),
    handicap_index DECIMAL(5,2)
);

CREATE TABLE events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50),
    event_date DATE,
    event_location VARCHAR(100)
);

CREATE TABLE ParticipantDetails (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(50),
    participant_email VARCHAR(50),
    participant_phone VARCHAR(15)
);

CREATE TABLE EventDates (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50),
    event_date DATE,
    event_location VARCHAR(100)
);
