tables.table_schema:
CREATE TABLE club_championships (
    club_name VARCHAR(50),
    championships_won INT
);

CREATE TABLE player_transfers (
    player_name VARCHAR(50),
    previous_club VARCHAR(50),
    new_club VARCHAR(50),
    transfer_fee DECIMAL(10, 2)
);

CREATE TABLE managerial_changes (
    club_name VARCHAR(50),
    manager_name VARCHAR(50),
    change_date DATE
);

CREATE TABLE championships (
    id INT PRIMARY KEY,
    club_name VARCHAR(255),
    year INT,
    championship_name VARCHAR(255)
);

CREATE TABLE managerial_changes (
    id INT PRIMARY KEY,
    club_name VARCHAR(255),
    year INT,
    manager_name VARCHAR(255)
);

CREATE TABLE clubs (
    id INT PRIMARY KEY,
    club_name VARCHAR(255),
    location VARCHAR(255),
    stadium_capacity INT
);
