tables.table_schema:
CREATE TABLE PitcherMetrics (
  pitcher_id INT PRIMARY KEY,
  ERA FLOAT,
  strikeouts INT,
  WHIP FLOAT
);

CREATE TABLE TeamRoster (
  team_id INT PRIMARY KEY,
  pitcher_id INT,
  player_name VARCHAR(255),
  jersey_number INT
);

CREATE TABLE games (
    game_id INT PRIMARY KEY,
    date DATE,
    stadium VARCHAR(50),
    attendance INT
);

CREATE TABLE teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    city VARCHAR(50),
    manager VARCHAR(50)
);
