tables.table_schema:
CREATE TABLE FootballClubs ( 
    club_id INT PRIMARY KEY, 
    club_name TEXT, 
    city TEXT, 
    stadium_name TEXT 
);

CREATE TABLE football_clubs (
    club_id INT PRIMARY KEY,
    club_name VARCHAR(100),
    city VARCHAR(50),
    stadium_capacity INT
);

CREATE TABLE sponsors (
    sponsor_id INT PRIMARY KEY,
    sponsor_name VARCHAR(100),
    industry VARCHAR(50),
    country VARCHAR(50)
);
