tables.table_schema:
CREATE TABLE venues (
    venue_id SERIAL PRIMARY KEY,
    venue_name VARCHAR(255),
    city VARCHAR(100),
    country VARCHAR(100)
);

CREATE TABLE fighters (
    fighter_id SERIAL PRIMARY KEY,
    name VARCHAR(100),
    weight_class VARCHAR(50),
    record VARCHAR(20)
);

CREATE TABLE locations (
    location_id SERIAL PRIMARY KEY,
    city VARCHAR(100),
    country VARCHAR(100)
);
