tables.table_schema:
CREATE TABLE Boxers (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    weight_class VARCHAR(50)
);

CREATE TABLE Boxers (
    boxer_id INT PRIMARY KEY,
    name VARCHAR(50),
    weight FLOAT,
    height FLOAT
);

CREATE TABLE Boxers (
    boxer_id INT PRIMARY KEY,
    boxer_name VARCHAR(50) NOT NULL,
    weight_class VARCHAR(20) NOT NULL
);

CREATE TABLE Titles (
    title_id INT PRIMARY KEY,
    boxer_id INT NOT NULL,
    title_name VARCHAR(50) NOT NULL,
    defense_date DATE
);
