tables.table_schema:
CREATE TABLE boxers (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    weight_class VARCHAR(50) NOT NULL
);

CREATE TABLE rankings (
    ranking_id INT PRIMARY KEY,
    boxer_id INT,
    rank INT,
    points INT
);

CREATE TABLE Boxers (
 boxer_id INT PRIMARY KEY,
 boxer_name VARCHAR(255),
 weight_class VARCHAR(50),
 country VARCHAR(50)
);

CREATE TABLE Rankings (
 ranking_id INT PRIMARY KEY,
 boxer_id INT,
 ranking INT,
 date DATE
);

CREATE TABLE Titles (
 title_id INT PRIMARY KEY,
 title_name VARCHAR(255),
 weight_class VARCHAR(50),
 current_holder_id INT
);
