tables.table_schema:
CREATE TABLE pricing_details (
  pricing_id INT PRIMARY KEY,
  equipment_id INT,
  price DECIMAL(10, 2),
  discount DECIMAL(5, 2)
);

CREATE TABLE boxing_equipment_inventory (
   equipment_id INT PRIMARY KEY,
   equipment_name VARCHAR(255),
   quantity INT,
   price DECIMAL(10, 2)
);

CREATE TABLE equipment_categories (
   category_id INT PRIMARY KEY,
   category_name VARCHAR(50),
   description TEXT
);

CREATE TABLE boxing_equipment (
  equipment_id UUID PRIMARY KEY,
  equipment_name VARCHAR(255) NOT NULL,
  equipment_type VARCHAR(100) NOT NULL,
  price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE equipment_inventory (
  inventory_id UUID PRIMARY KEY,
  equipment_id UUID REFERENCES boxing_equipment(equipment_id),
  quantity INT NOT NULL
);

CREATE TABLE pricing_details (
  pricing_id UUID PRIMARY KEY,
  equipment_id UUID REFERENCES boxing_equipment(equipment_id),
  price DECIMAL(10, 2) NOT NULL,
  discount DECIMAL(5, 2)
);

CREATE TABLE manufacturers (
  manufacturer_id UUID PRIMARY KEY,
  manufacturer_name VARCHAR(255) NOT NULL
);

CREATE TABLE equipment_manufacturer (
  equipment_id UUID REFERENCES boxing_equipment(equipment_id),
  manufacturer_id UUID REFERENCES manufacturers(manufacturer_id),
  PRIMARY KEY (equipment_id, manufacturer_id)
);
