tables.table_schema:
CREATE TABLE MedicalRecords (
    record_id SERIAL PRIMARY KEY,
    boxer_name VARCHAR(100),
    date_of_birth DATE,
    blood_type VARCHAR(5)
);

CREATE TABLE InjuryHistory (
    injury_id SERIAL PRIMARY KEY,
    boxer_name VARCHAR(100),
    injury_type VARCHAR(50),
    date_of_injury DATE
);

CREATE TABLE medical_records (
  record_id INT PRIMARY KEY,
  boxer_id INT NOT NULL,
  record_date DATE,
  diagnosis TEXT
);

CREATE TABLE injuries (
  injury_id INT PRIMARY KEY,
  boxer_id INT,
  injury_date DATE,
  injury_type VARCHAR(50)
);

CREATE TABLE Gyms (
    gym_id INT PRIMARY KEY,
    name VARCHAR(50),
    location VARCHAR(100),
    trainer VARCHAR(50)
);
