tables.table_schema:
CREATE TABLE boxing_coaches (
    coach_id INT PRIMARY KEY,
    coach_name VARCHAR(50),
    coach_phone VARCHAR(15),
    coach_email VARCHAR(50)
);

CREATE TABLE BoxingCoaches (
  coach_id INT PRIMARY KEY,
  coach_name VARCHAR(50) NOT NULL,
  coach_email VARCHAR(50) UNIQUE,
  coach_experience INT
);

CREATE TABLE BoxingCoaches (
    coach_id UUID PRIMARY KEY,
    coach_name VARCHAR(50),
    coach_email VARCHAR(100),
    coaching_experience INT
);

CREATE TABLE Trainees (
    trainee_id UUID PRIMARY KEY,
    trainee_name VARCHAR(50),
    trainee_age INT,
    trainee_gender VARCHAR(10)
);

CREATE TABLE Coaches (
	coach_id UUID,
	coach_name VARCHAR(255),
	coach_email VARCHAR(255),
	coach_phone VARCHAR(15)
);

CREATE TABLE Coaches (
   coach_id INTEGER PRIMARY KEY,
   coach_name VARCHAR(50) NOT NULL,
   coach_email VARCHAR(100) UNIQUE,
   coach_phone VARCHAR(15)
);

CREATE TABLE Sessions (
   session_id INTEGER PRIMARY KEY,
   session_date DATE NOT NULL,
   session_duration INTEGER,
   trainee_id INTEGER REFERENCES Trainees(trainee_id)
);

CREATE TABLE SessionNotes (
   note_id INTEGER PRIMARY KEY,
   note_text TEXT,
   session_id INTEGER REFERENCES Sessions(session_id)
);

CREATE TABLE Payments (
   payment_id INTEGER PRIMARY KEY,
   payment_amount DECIMAL(10, 2) NOT NULL,
   payment_date DATE,
   trainee_id INTEGER REFERENCES Trainees(trainee_id)
);
