CREATE TABLE team_roster (
    team_id INT,
    player_name VARCHAR(50),
    position VARCHAR(50),
    is_active BOOL
);

CREATE TABLE team_roster (
    team_id INT PRIMARY KEY,
    team_name VARCHAR,
    active_players INT,
    player_positions JSONB
);

CREATE TABLE player_info (
    player_id INT PRIMARY KEY,
    player_name VARCHAR,
    team_id INT,
    position VARCHAR
);

CREATE TABLE team_roster (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(100),
    coach_name VARCHAR(100)
);

CREATE TABLE player (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(100),
    position VARCHAR(50),
    team_id INT REFERENCES team_roster(team_id)
);

CREATE TABLE game (
    game_id INT PRIMARY KEY,
    game_date DATE,
    team1_id INT REFERENCES team_roster(team_id),
    team2_id INT REFERENCES team_roster(team_id)
);

CREATE TABLE team (team_id INT PRIMARY KEY,
            team_name VARCHAR(50) NOT NULL,
            coach_name VARCHAR(50) NOT NULL);

CREATE TABLE game (game_id INT PRIMARY KEY,
            game_date DATE NOT NULL,
            location VARCHAR(100) NOT NULL);

CREATE TABLE Team (
    team_id UUID PRIMARY KEY,
    team_name VARCHAR(255) NOT NULL
);

CREATE TABLE Player (
    player_id UUID PRIMARY KEY,
    player_name VARCHAR(255) NOT NULL,
    position VARCHAR(50)
);

CREATE TABLE Position (
    position_id UUID PRIMARY KEY,
    position_name VARCHAR(50) NOT NULL
);
