tables.table_schema:
CREATE TABLE teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50) NOT NULL
);

CREATE TABLE coaches (
    coach_id INT PRIMARY KEY,
    coach_name VARCHAR(50) NOT NULL,
    team_id INT NOT NULL,
    role VARCHAR(20) NOT NULL,
    FOREIGN KEY (team_id) REFERENCES teams(team_id)
);

CREATE TABLE head_coach (
  team_id INT,
  coach_name VARCHAR(255)
);

CREATE TABLE assistant_coach (
  team_id INT,
  assistant_coach_name VARCHAR(255)
);

CREATE TABLE team (
  team_id INT,
  team_name VARCHAR(255)
);

CREATE TABLE teams (
 team_id INT PRIMARY KEY,
 team_name VARCHAR (50) NOT NULL,
 head_coach VARCHAR (50) NOT NULL
);

CREATE TABLE head_coaches (
 head_coach_id INT PRIMARY KEY,
 head_coach_name VARCHAR (50) NOT NULL
);

CREATE TABLE assistant_coaches (
 assistant_coach_id INT PRIMARY KEY,
 assistant_coach_name VARCHAR (50) NOT NULL
);

CREATE TABLE team_coaches (
 team_coach_id SERIAL PRIMARY KEY,
 team_id INT REFERENCES teams(team_id),
 head_coach_id INT REFERENCES head_coaches(head_coach_id),
 assistant_coach_id INT REFERENCES assistant_coaches(assistant_coach_id)
);
