tables.table_schema:
CREATE TABLE team_championships (
    team_id UUID PRIMARY KEY,
    team_name VARCHAR(50) NOT NULL,
    championships_won INT,
    runner_up_finishes INT,
    playoff_appearances INT
);

CREATE TABLE playoff_history (
    team_id UUID PRIMARY KEY,
    team_name VARCHAR(50) NOT NULL,
    playoff_season INT,
    result VARCHAR(20)
);

CREATE TABLE TeamChampionships (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(100) NOT NULL,
  championships_won INT,
);

CREATE TABLE RunnerUpFinishes (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(100) NOT NULL,
  runner_up_finishes INT,
);

CREATE TABLE PlayoffAppearances (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(100) NOT NULL,
  playoff_appearances INT,
);

CREATE TABLE team_players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    team_id INT,
    FOREIGN KEY (team_id) REFERENCES team_championships(team_id)
);
