tables.table_schema:
CREATE TABLE wrestler_profiles (
    name VARCHAR,
    age INT,
    weight INT,
    championships TEXT
);

CREATE TABLE championships (
    championship_id SERIAL PRIMARY KEY,
    championship_name VARCHAR(50) NOT NULL,
    wrestler_id INT NOT NULL,
    FOREIGN KEY (wrestler_id) REFERENCES wrestler_profiles(id)
);

CREATE TABLE promotions (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    location VARCHAR(50)
);

CREATE TABLE championship_titles (
    title_id SERIAL PRIMARY KEY,
    title_name VARCHAR(50) NOT NULL,
    current_champion VARCHAR(50),
    weight_class VARCHAR(20)
);

CREATE TABLE championships (
    id INT PRIMARY KEY,
    championship_name VARCHAR(50),
    year INT
);

CREATE TABLE promotions (
    id INT PRIMARY KEY,
    promotion_name VARCHAR(50),
    location VARCHAR(50)
);

CREATE TABLE events (
    id INT PRIMARY KEY,
    event_name VARCHAR(50),
    date DATE,
    promotion_id INT
);
