tables.table_schema:
CREATE TABLE wrestlers (
    wrestler_id INT PRIMARY KEY,
    wrestler_name VARCHAR(255),
    win_count INT,
    loss_count INT
);

CREATE TABLE finishing_moves (
    move_id INT PRIMARY KEY,
    move_name VARCHAR(255),
    wrestler_id INT,
    FOREIGN KEY (wrestler_id) REFERENCES wrestlers(wrestler_id)
);

CREATE TABLE match_statistics (
    match_id INT PRIMARY KEY,
    wrestler_id INT,
    finish_move_id INT,
    match_duration TIME,
    FOREIGN KEY (wrestler_id) REFERENCES wrestlers(wrestler_id),
    FOREIGN KEY (finish_move_id) REFERENCES finishing_moves(move_id)
);

CREATE TABLE wrestler_stats (
  wrestler_id INT PRIMARY KEY,
  wrestler_name VARCHAR(255),
  win_count INT,
  loss_count INT
);

CREATE TABLE match_stats (
  match_id INT PRIMARY KEY,
  wrestler_id INT,
  date DATE,
  duration TIME
);

CREATE TABLE finishing_moves (
  move_id INT PRIMARY KEY,
  move_name VARCHAR(255)
);

CREATE TABLE wrestler_moves (
  wrestler_id INT,
  move_id INT
);
