tables.table_schema:
CREATE TABLE MatchResults (
    match_id INT PRIMARY KEY,
    wrestler_id INT,
    opponent_id INT,
    result VARCHAR(10) NOT NULL
);

CREATE TABLE Wrestlers (
    wrestler_id SERIAL PRIMARY KEY,
    wrestler_name VARCHAR(50) NOT NULL,
    wrestler_ranking INT,
    wrestler_popularity INT
);

CREATE TABLE Matches (
    match_id SERIAL PRIMARY KEY,
    wrestler_id INT REFERENCES Wrestlers(wrestler_id),
    match_date TIMESTAMP,
    match_result VARCHAR(50) 
);

CREATE TABLE Rankings (
    ranking_id SERIAL PRIMARY KEY,
    wrestler_id INT REFERENCES Wrestlers(wrestler_id),
    ranking_date DATE,
    ranking_position INT 
);

CREATE TABLE Rankings (
    ranking_id INT PRIMARY KEY,
    wrestler_id INT,
    rank_date DATE NOT NULL,
    rank_position INT
);

CREATE TABLE Fans (
    fan_id INT PRIMARY KEY,
    fan_name VARCHAR(50) NOT NULL,
    fan_email VARCHAR(50) UNIQUE
);

CREATE TABLE promotions (
    promotion_id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    country VARCHAR(100),
    creation_date DATE
);

CREATE TABLE fans (
    fan_id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    location VARCHAR(100),
    favorite_wrestler_id UUID
);
