tables.table_schema:
CREATE TABLE training_programs (
  program_id UUID PRIMARY KEY,
  program_name VARCHAR(255) NOT NULL,
  program_description TEXT
);

CREATE TABLE coaches (
  coach_id UUID PRIMARY KEY,
  coach_name VARCHAR(255) NOT NULL,
  coach_email VARCHAR(255) UNIQUE
);

CREATE TABLE training_sessions (
  session_id UUID PRIMARY KEY,
  program_id UUID REFERENCES training_programs(program_id),
  coach_id UUID REFERENCES coaches(coach_id),
  session_date DATE
);

CREATE TABLE training_programs (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(255),
    program_description TEXT
);

CREATE TABLE coaches (
    coach_id INT PRIMARY KEY,
    coach_name VARCHAR(255),
    coach_specialty VARCHAR(255)
);

CREATE TABLE training_sessions (
    session_id INT PRIMARY KEY,
    session_date DATE,
    session_description TEXT
);

CREATE TABLE wrestlers (
    wrestler_id INT PRIMARY KEY,
    wrestler_name VARCHAR(255),
    wrestler_age INT,
    wrestler_weight INT
);
