tables.table_schema:
CREATE TABLE Audit_Log (
  id UUID PRIMARY KEY,
  event_time TIMESTAMP,
  event_type VARCHAR,
  event_description TEXT
);

CREATE TABLE Security_Threats (
  threat_id SERIAL PRIMARY KEY,
  threat_description TEXT,
  threat_level VARCHAR
);

CREATE TABLE security_incidents (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    description TEXT,
    severity INT
);
