tables.table_schema:
CREATE TABLE encrypted_data (
    id INT PRIMARY KEY,
    sensitive_data VARCHAR(255),
    encrypted_data VARBINARY,
    transmission_encryption_key VARCHAR(50)
);

CREATE TABLE users (
    id UUID PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(100),
    password TEXT
);

CREATE TABLE sensitive_data (
    id UUID PRIMARY KEY,
    user_id UUID REFERENCES users (id),
    credit_card_number VARCHAR(16),
    social_security_number VARCHAR(9)
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE customers (
    customer_id SERIAL PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE
);

CREATE TABLE orders (
    order_id SERIAL PRIMARY KEY,
    customer_id INT NOT NULL,
    total_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE products (
    product_id SERIAL PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    price DECIMAL(8, 2) NOT NULL
);

CREATE TABLE data_encryption (
    id INT PRIMARY KEY,
    sensitive_data VARCHAR(255),
    encrypted_data BYTEA
);

CREATE TABLE data_transmission_security (
    id INT PRIMARY KEY,
    source_address VARCHAR(50),
    destination_address VARCHAR(50),
    encryption_key VARCHAR(100)
);

CREATE TABLE compliance_regulations (
    id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    compliance_status BOOLEAN
);

CREATE TABLE user_authentication (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    password VARCHAR(50),
    two_factor_authentication BOOLEAN
);

CREATE TABLE employee_access_rights (
    id INT PRIMARY KEY,
    employee_id INT,
    access_level VARCHAR(50)
);
