tables.table_schema:
CREATE TABLE suspicious_activities (
    id INT PRIMARY KEY,
    event_type VARCHAR(100),
    timestamp TIMESTAMP,
    description TEXT
);

CREATE TABLE suspicious_activities (
    activity_id INT PRIMARY KEY,
    log_id INT REFERENCES network_traffic_logs(log_id),
    activity_description VARCHAR
);

CREATE TABLE detected_threats (
    threat_id INT PRIMARY KEY,
    activity_id INT REFERENCES suspicious_activities(activity_id),
    threat_level VARCHAR
);

CREATE TABLE user_accounts (
    id UUID PRIMARY KEY,
    username VARCHAR(50),
    password VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE user_logs (
    id UUID PRIMARY KEY,
    user_id UUID REFERENCES user_accounts(id),
    action VARCHAR(50),
    timestamp TIMESTAMPTZ
);

CREATE TABLE system_logs (
    id UUID PRIMARY KEY,
    log_type VARCHAR(50),
    message TEXT,
    timestamp TIMESTAMPTZ
);
