  change_timestamp TIMESTAMP
);

CREATE TABLE network_configuration_changes (
  id INT PRIMARY KEY,
  change_description TEXT,
  change_timestamp TIMESTAMP
);

CREATE TABLE recording_system_changes (
    id SERIAL PRIMARY KEY,
    change_time TIMESTAMP,
    change_description TEXT,
    user_id INT
);

CREATE TABLE network_configuration_changes (
    id SERIAL PRIMARY KEY,
    change_time TIMESTAMP,
    change_description TEXT,
    device_id INT
);

CREATE TABLE recordings (
    id INT PRIMARY KEY,
    user_name VARCHAR,
    change_description VARCHAR,
    change_timestamp TIMESTAMP
);

CREATE TABLE network_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR,
    device_type VARCHAR,
    ip_address VARCHAR
);

CREATE TABLE user_activities (
    activity_id INT PRIMARY KEY,
    user_id INT,
    action_taken VARCHAR,
    activity_timestamp TIMESTAMP
);

CREATE TABLE network_changes (
    change_id INT PRIMARY KEY,
    device_id INT,
    change_description VARCHAR,
    change_timestamp TIMESTAMP
);

CREATE TABLE audit_trails (
    trail_id INT PRIMARY KEY,
    user_id INT,
    activity_description VARCHAR,
    trail_timestamp TIMESTAMP
);
