    key_id UUID PRIMARY KEY,
    key_value BYTES NOT NULL,
    created_at TIMESTAMP DEFAULT NOW()
);

CREATE TABLE encryption_keys (
    id UUID PRIMARY KEY,
    key_name VARCHAR(50) NOT NULL,
