);

CREATE TABLE encrypted_messages (
    message_id UUID PRIMARY KEY,
    message_text TEXT NOT NULL,
    encryption_key_id UUID NOT NULL,
    FOREIGN KEY (encryption_key_id) REFERENCES encryption_keys(id)
);

