tables.table_schema:
CREATE TABLE FloorPlans (
    floor_id INT,
    floor_name VARCHAR(255),
    layout BLOB,
    PRIMARY KEY (floor_id)
);

CREATE TABLE AccessControlPoints (
    access_point_id INT,
    location_id INT,
    access_level VARCHAR(50),
    PRIMARY KEY (access_point_id),
    FOREIGN KEY (location_id) REFERENCES FloorPlans(floor_id)
);

CREATE TABLE FloorPlans (
    floor_id INT PRIMARY KEY,
    floor_plan_image BYTEA,
    access_control_points TEXT
);

CREATE TABLE BuildingLayouts (
    layout_id INT PRIMARY KEY,
    building_name VARCHAR(50),
    floors INT
);

CREATE TABLE AccessControlPoints (
    point_id INT PRIMARY KEY,
    point_name VARCHAR(50),
    location VARCHAR(100),
    accessibility BOOLEAN
);

CREATE TABLE FloorPlans (
	p_id INT PRIMARY KEY,
	floor_name VARCHAR(50),
	building_name VARCHAR(50),
	image_url VARCHAR(255)
);

CREATE TABLE AccessControlPoints (
	acp_id INT PRIMARY KEY,
	location VARCHAR(100),
	floor_id INT,
	FOREIGN KEY (floor_id) REFERENCES FloorPlans(p_id)
);

CREATE TABLE Emergencies (
	e_id INT PRIMARY KEY,
	description TEXT,
	date_time TIMESTAMP
);
