tables.table_schema:
CREATE TABLE SecurityPersonnel (
    personnel_id INT PRIMARY KEY,
    personnel_name VARCHAR,
    personnel_role VARCHAR,
    hourly_rate FLOAT
);

CREATE TABLE Shifts (
    shift_id INT PRIMARY KEY,
    shift_date DATE,
    shift_type VARCHAR,
    personnel_id INT REFERENCES SecurityPersonnel(personnel_id)
);

CREATE TABLE ShiftAssignments (
    assignment_id INT PRIMARY KEY,
    shift_id INT REFERENCES Shifts(shift_id),
    personnel_id INT REFERENCES SecurityPersonnel(personnel_id)
);

CREATE TABLE Locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR
);

CREATE TABLE ShiftLocations (
    shift_id INT REFERENCES Shifts(shift_id),
    location_id INT REFERENCES Locations(location_id)
);
