tables.table_schema:
CREATE TABLE SecurityPatches (
    patch_id SERIAL PRIMARY KEY,
    patch_name VARCHAR(100) NOT NULL,
    release_date DATE NOT NULL,
    vulnerability_score INT
);

CREATE TABLE Vulnerabilities (
    vulnerability_id SERIAL PRIMARY KEY,
    vulnerability_name VARCHAR(100) NOT NULL,
    description TEXT,
    patch_id INT REFERENCES SecurityPatches(patch_id)
);

CREATE TABLE security_patches (
	patch_id INT PRIMARY KEY,
	patch_name VARCHAR(50) NOT NULL,
	patch_description TEXT,
	patch_date DATE
);

CREATE TABLE vulnerability_list (
	vulnerability_id INT PRIMARY KEY,
	vulnerability_name VARCHAR(50) NOT NULL,
	vulnerability_description TEXT,
	vulnerability_severity ENUM('low', 'medium', 'high' )
);
