tables.table_schema:
CREATE TABLE door_events (
  event_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  door_id INT,
  event_type VARCHAR
);

CREATE TABLE doors (
  door_id SERIAL PRIMARY KEY,
  door_name VARCHAR,
  location VARCHAR
);

CREATE TABLE employees (
  employee_id SERIAL PRIMARY KEY,
  employee_name VARCHAR,
  department VARCHAR
);

CREATE TABLE door_entries (
  entry_id UUID PRIMARY KEY,
  entry_time TIMESTAMP NOT NULL,
  entry_type VARCHAR(50) NOT NULL,
  user_id UUID NOT NULL
);

CREATE TABLE users (
  user_id UUID PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  email VARCHAR(255) UNIQUE NOT NULL
);

CREATE TABLE access_levels (
  level_id UUID PRIMARY KEY,
  level_name VARCHAR(50) NOT NULL,
  description TEXT
);

CREATE TABLE employees (
    employee_id UUID PRIMARY KEY,
    employee_name VARCHAR(255),
    department VARCHAR(255),
    position VARCHAR(255)
);

CREATE TABLE doors (
    door_id UUID PRIMARY KEY,
    door_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE entry_logs (
    log_id UUID PRIMARY KEY,
    entry_id UUID REFERENCES door_entries(entry_id),
    event_description TEXT,
    logged_time TIMESTAMP
);

CREATE TABLE access_levels (
    level_id UUID PRIMARY KEY,
    employee_id UUID REFERENCES employees(employee_id),
    door_id UUID REFERENCES doors(door_id),
    access_level INT
);
