CREATE TABLE schedule (
    schedule_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL,
    light_intensity FLOAT NOT NULL
);

CREATE TABLE user_preferences (
    user_id UUID PRIMARY KEY,
    preferred_light_intensity FLOAT NOT NULL,
    preferred_start_time TIME NOT NULL,
    preferred_end_time TIME NOT NULL
);

CREATE TABLE light_groups (
    group_id UUID PRIMARY KEY,
    group_name VARCHAR(50) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    timezone VARCHAR(50) NOT NULL
);

CREATE TABLE lights (
    light_id INT PRIMARY KEY,
    light_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    status BOOLEAN NOT NULL
);

CREATE TABLE lighting_controls (
    lighting_control_id INT PRIMARY KEY,
    control_name TEXT,
    intensity INT,
    status TEXT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    name TEXT,
    email TEXT,
    phone_number TEXT
);
