tables.table_schema:
CREATE TABLE motion_sensor_data (
  id SERIAL PRIMARY KEY,
  sensor_id INT,
  activation_timestamp TIMESTAMP,
  location VARCHAR(100)
);

CREATE TABLE intruder_alerts (
  alert_id SERIAL PRIMARY KEY,
  sensor_id INT,
  alert_timestamp TIMESTAMP,
  alert_type VARCHAR(50)
);

CREATE TABLE motion_sensors (
  id SERIAL PRIMARY KEY,
  sensor_id INT NOT NULL,
  activated_at TIMESTAMP NOT NULL,
  location VARCHAR(50) NOT NULL
);

CREATE TABLE intruders (
  id SERIAL PRIMARY KEY,
  intruder_id INT NOT NULL,
  authorized BOOLEAN NOT NULL,
  captured_at TIMESTAMP NOT NULL
);

CREATE TABLE intruder_locations (
  id SERIAL PRIMARY KEY,
  intruder_id INT NOT NULL,
  location VARCHAR(50) NOT NULL,
  motion_sensor_id INT
);

CREATE TABLE motion_sensor_activations (
    event_id INT PRIMARY KEY,
    sensor_id INT,
    motion_detected BOOLEAN,
    timestamp TIMESTAMP
);

CREATE TABLE sensors (
    sensor_id INT PRIMARY KEY,
    location VARCHAR(255),
    battery_level FLOAT
);

CREATE TABLE intruders (
    intruder_id INT PRIMARY KEY,
    name VARCHAR(255),
    captured_image BYTEA
);

CREATE TABLE alerts (
    alert_id INT PRIMARY KEY,
    sensor_id INT,
    timestamp TIMESTAMP,
    message VARCHAR(255)
);

CREATE TABLE sensors (
  sensor_id UUID PRIMARY KEY,
  sensor_name VARCHAR NOT NULL,
  location VARCHAR NOT NULL
);

CREATE TABLE intruder_data (
  intruder_id UUID PRIMARY KEY,
  detected_time TIMESTAMP NOT NULL,
  location VARCHAR NOT NULL
);

CREATE TABLE sensor_logs (
  log_id UUID PRIMARY KEY,
  sensor_id UUID NOT NULL,
  log_time TIMESTAMP NOT NULL,
  log_data JSONB
);

CREATE TABLE intruder_images (
  image_id UUID PRIMARY KEY,
  intruder_id UUID NOT NULL,
  image_data BYTEA NOT NULL
);
