tables.table_schema:
CREATE TABLE alarm_triggers (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP
);

CREATE TABLE incident_response (
  incident_id INT PRIMARY KEY,
  incident_name VARCHAR(255),
  responder_name VARCHAR(255),
  response_timestamp TIMESTAMP
);

CREATE TABLE alarm_incident_mapping (
  alarm_id INT,
  incident_id INT,
  PRIMARY KEY (alarm_id, incident_id)
);

CREATE TABLE alarm_triggers ( 
    id INT PRIMARY KEY, 
    timestamp TIMESTAMP, 
    alarm_type VARCHAR(255) 
);

CREATE TABLE incident_response ( 
    id INT PRIMARY KEY, 
    incident_id INT, 
    response_team VARCHAR(255), 
    timestamp TIMESTAMP 
);

CREATE TABLE incident_details ( 
    incident_id INT, 
    location VARCHAR(255), 
    description TEXT 
);

CREATE TABLE alarm_types ( 
    type_id INT PRIMARY KEY, 
    alarm_name VARCHAR(255) 
);

CREATE TABLE response_teams ( 
    team_id INT PRIMARY KEY, 
    team_name VARCHAR(255) 
);
