tables.table_schema:
CREATE TABLE security_cameras (
  camera_id INT PRIMARY KEY,
  location VARCHAR(255),
  install_date DATE
);

CREATE TABLE camera_footage (
    footage_id INT PRIMARY KEY,
    camera_id INT,
    timestamp TIMESTAMPTZ,
    video_file_path VARCHAR
);

CREATE TABLE footage_analysis (
    analysis_id INT PRIMARY KEY,
    footage_id INT,
    analysis_result VARCHAR,
    analysis_timestamp TIMESTAMPTZ
);

CREATE TABLE cameras (
    camera_id INT PRIMARY KEY,
    location VARCHAR,
    resolution VARCHAR
);
