tables.table_schema:
CREATE TABLE EmergencyContacts (
    contact_id INT PRIMARY KEY,
    contact_name VARCHAR(255) NOT NULL,
    contact_phone VARCHAR(15) NOT NULL,
    relationship VARCHAR(50) NOT NULL
);

CREATE TABLE SecurityBreaches (
    breach_id INT PRIMARY KEY,
    breach_date DATE NOT NULL,
    breach_location VARCHAR(100) NOT NULL,
    breach_description TEXT
);

CREATE TABLE ContactRelationship (
    relationship_id INT PRIMARY KEY,
    relationship_type VARCHAR(50) NOT NULL
);

CREATE TABLE emergency_contacts (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    phone_number VARCHAR(15) NOT NULL,
    email VARCHAR(50) NOT NULL
);

CREATE TABLE security_breaches (
    id INT PRIMARY KEY,
    location VARCHAR(50) NOT NULL,
    time TIMESTAMP NOT NULL,
    severity INT NOT NULL
);

CREATE TABLE contact_logs (
    id INT PRIMARY KEY,
    contact_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    description TEXT
);

CREATE TABLE response_teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50) NOT NULL,
    location VARCHAR(50) NOT NULL
);
