tables.table_schema:
CREATE TABLE incident_analysis (
    analysis_id INT PRIMARY KEY,
    incident_id INT,
    analysis_date TIMESTAMP,
    analysis_result TEXT
);

CREATE TABLE incident_trends (
    trend_id INT PRIMARY KEY,
    incident_type VARCHAR(50),
    trend_description TEXT,
    trend_date DATE
);

CREATE TABLE security_incidents (
    incident_id INT PRIMARY KEY,
    incident_date DATE,
    incident_description TEXT,
    location VARCHAR(255),
    severity INT
);

CREATE TABLE incident_resolutions (
    resolution_id INT PRIMARY KEY,
    incident_id INT,
    resolution_date DATE,
    resolution_details TEXT,
    resolution_status VARCHAR(50)
);

CREATE TABLE incident_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE incident_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(100),
    participant_role VARCHAR(50)
);

CREATE TABLE incident_files (
    file_id INT PRIMARY KEY,
    incident_id INT,
    file_name VARCHAR(255),
    file_type VARCHAR(50)
);
