tables.table_schema:
CREATE TABLE security_incidents (
    incident_id SERIAL PRIMARY KEY,
    incident_date DATE NOT NULL,
    incident_type VARCHAR(50) NOT NULL,
    incident_description TEXT
);

CREATE TABLE incident_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_description TEXT,
    detection_method VARCHAR(255),
    classification VARCHAR(255)
);

CREATE TABLE security_incidents (
    incident_id SERIAL PRIMARY KEY,
    incident_type VARCHAR(50) NOT NULL,
    incident_date DATE NOT NULL,
    incident_description TEXT
);

CREATE TABLE attack_patterns (
    pattern_id SERIAL PRIMARY KEY,
    pattern_name VARCHAR(50) NOT NULL,
    pattern_description TEXT
);

CREATE TABLE attackers (
    attacker_id SERIAL PRIMARY KEY,
    attacker_name VARCHAR(50) NOT NULL,
    attacker_country VARCHAR(50),
    attack_method VARCHAR(50)
);

CREATE TABLE affected_assets (
    asset_id SERIAL PRIMARY KEY,
    asset_name VARCHAR(50) NOT NULL,
    asset_type VARCHAR(50),
    asset_description TEXT
);

CREATE TABLE incident_logs (
    log_id SERIAL PRIMARY KEY,
    incident_id INT REFERENCES security_incidents(incident_id),
    log_timestamp TIMESTAMP NOT NULL,
    log_details TEXT
);
