tables.table_schema:
CREATE TABLE suspicious_activities (
  id SERIAL PRIMARY KEY,
  activity_id INT,
  detected_at TIMESTAMP,
  description TEXT
);

CREATE TABLE sql_activity_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    activity_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE activity_suspicious_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    activity_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE suspicious_activity_alerts (
    alert_id INT PRIMARY KEY,
    log_id INT,
    alert_message TEXT,
    alert_timestamp TIMESTAMP
);

CREATE TABLE logs (
    log_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    user_id INT,
    query TEXT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    role VARCHAR(20)
);

CREATE TABLE suspicious_activities (
    activity_id INT PRIMARY KEY,
    log_id INT,
    timestamp TIMESTAMP,
    activity_description TEXT
);
