tables.table_schema:
CREATE TABLE security_incidents (
    incident_id INT PRIMARY KEY,
    incident_type TEXT,
    incident_description TEXT,
    incident_date TIMESTAMP
);

CREATE TABLE security_logs (
    log_id INT PRIMARY KEY,
    incident_id INT REFERENCES security_incidents(incident_id),
    log_description TEXT,
    log_date TIMESTAMP
);

CREATE TABLE IncidentLogs (
    log_id INT PRIMARY KEY,
    incident_id INT REFERENCES SecurityIncidents(incident_id),
    log_date TIMESTAMP,
    log_description TEXT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(50)
);

CREATE TABLE incident_logs (
    log_id INT PRIMARY KEY,
    incident_id INT,
    log_date DATE,
    details TEXT
);

CREATE TABLE breach_logs (
    log_id INT PRIMARY KEY,
    breach_id INT,
    log_date DATE,
    details TEXT
);

CREATE TABLE defense_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(100),
    description TEXT
);
