tables.table_schema:
CREATE TABLE secure_connections (
    id SERIAL PRIMARY KEY,
    client_id INT,
    server_id INT,
    connection_status VARCHAR(50)
);

CREATE TABLE secure_connections (
    id SERIAL PRIMARY KEY,
    connection_name VARCHAR(50) NOT NULL,
    connection_type VARCHAR(20) NOT NULL,
    certificate_file BYTEA NOT NULL
);

CREATE TABLE ssl_certificates (
    certificate_id SERIAL PRIMARY KEY,
    certificate_name VARCHAR(50) NOT NULL,
    expiration_date DATE NOT NULL,
    issuer_name VARCHAR(50) NOT NULL
);

CREATE TABLE communication_channel (
    channel_id SERIAL PRIMARY KEY,
    channel_name VARCHAR(50) NOT NULL,
    channel_type VARCHAR(20) NOT NULL,
    associated_cert_id INTEGER REFERENCES ssl_certificates(certificate_id)
);

CREATE TABLE user_credentials (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE ssl_certificates(
    certificate_id SERIAL PRIMARY KEY,
    certificate_name TEXT NOT NULL,
    certificate_data BYTEA NOT NULL
);

CREATE TABLE security_tools(
    tool_id SERIAL PRIMARY KEY,
    tool_name TEXT NOT NULL,
    tool_description TEXT
);
