tables.table_schema:
CREATE TABLE network_zones (
    zone_id INT PRIMARY KEY,
    zone_name VARCHAR(50) NOT NULL,
    zone_description TEXT,
    zone_subnet VARCHAR(20) NOT NULL
);

CREATE TABLE network_settings (
    setting_id SERIAL PRIMARY KEY,
    network_name VARCHAR(255) NOT NULL,
    network_address VARCHAR(255) NOT NULL,
    subnet_mask VARCHAR(255) NOT NULL
);

CREATE TABLE rule_audit_logs (
    log_id SERIAL PRIMARY KEY,
    rule_id INT REFERENCES firewall_rules_config(rule_id),
    action VARCHAR(50) NOT NULL,
    timestamp TIMESTAMPTZ DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE user_roles (
    role_id SERIAL PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL,
    role_description TEXT
);

CREATE TABLE firewall_settings (
  id INT PRIMARY KEY,
  rule_name VARCHAR(255),
  rule_type VARCHAR(50),
  allow_traffic BOOLEAN
);

CREATE TABLE network_zones (
  id INT PRIMARY KEY,
  zone_name VARCHAR(255),
  zone_description TEXT
);

CREATE TABLE zone_rules (
  id INT PRIMARY KEY,
  zone_id INT REFERENCES network_zones(id),
  rule_id INT REFERENCES firewall_settings(id)
);
