CREATE TABLE user_interactions (
    user_id INT,
    post_id INT,
    action VARCHAR(10),
    timestamp TIMESTAMP
);

CREATE TABLE post_interactions (
    post_id INT,
    user_id INT,
    interaction_type VARCHAR(10)
);

CREATE TABLE user_profile (
    user_id INT,
    username VARCHAR(50),
    email VARCHAR(100),
    user_type VARCHAR(20)
);

CREATE TABLE posts (
  post_id INT PRIMARY KEY,
  post_content TEXT
);

