CREATE TABLE editorial_calendars (
  id INT PRIMARY KEY,
  story_title VARCHAR(255),
  story_deadline TIMESTAMP,
  assigned_to VARCHAR(100)
);

CREATE TABLE editorial_calendars (
    calendar_id SERIAL PRIMARY KEY,
    calendar_name VARCHAR(50) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);

CREATE TABLE assignment_schedules (
    schedule_id SERIAL PRIMARY KEY,
    assignment_name VARCHAR(100) NOT NULL,
    assigned_editor VARCHAR(50) NOT NULL,
    deadline DATE NOT NULL
);

CREATE TABLE upcoming_stories (
    story_id SERIAL PRIMARY KEY,
    story_title VARCHAR(200) NOT NULL,
    story_content TEXT NOT NULL,
    publishing_date DATE NOT NULL
);

CREATE TABLE editorial_calendars (
    id INT PRIMARY KEY,
    event_date DATE,
    event_type VARCHAR(255),
    event_description TEXT
);

CREATE TABLE upcoming_stories (
    id INT PRIMARY KEY,
    story_title VARCHAR(255),
    story_content TEXT,
    publication_date DATE
);

CREATE TABLE deadlines (
    id INT PRIMARY KEY,
    story_id INT,
    deadline_date DATE,
    FOREIGN KEY (story_id) REFERENCES upcoming_stories(id)
);
