tables.table_schema:
CREATE TABLE Show_Schedule ( 
    show_id INT PRIMARY KEY, 
    show_name TEXT NOT NULL, 
    schedule_time TIMESTAMP NOT NULL 
);

CREATE TABLE ShowSchedule (
    show_id SERIAL PRIMARY KEY,
    show_name VARCHAR(255) NOT NULL,
    show_type VARCHAR(50) NOT NULL,
    schedule_time TIMESTAMP NOT NULL
);

CREATE TABLE ViewerRatings (
    rating_id SERIAL PRIMARY KEY,
    show_id INT REFERENCES ShowSchedule(show_id),
    viewer_name VARCHAR(100) NOT NULL,
    rating FLOAT NOT NULL,
    comment TEXT
);

CREATE TABLE show_schedule (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(255) NOT NULL,
    show_time TIME NOT NULL
);

CREATE TABLE episode_air_dates (
    episode_id INT PRIMARY KEY,
    episode_title VARCHAR(255) NOT NULL,
    episode_date DATE NOT NULL
);

CREATE TABLE shows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(255) NOT NULL,
    genre VARCHAR(100),
    rating FLOAT
);

CREATE TABLE viewers (
    viewer_id INT PRIMARY KEY,
    viewer_name VARCHAR(255) NOT NULL,
    age INT,
    gender VARCHAR(10)
);
