tables.table_schema:
CREATE TABLE privacy_settings (
    user_id INT,
    view_posts BOOLEAN,
    view_profile_info BOOLEAN
);

CREATE TABLE user_preferences (
    user_id INT,
    view_posts_permission BOOLEAN,
    view_profile_permission BOOLEAN
);

CREATE TABLE user_posts (
    post_id INT,
    user_id INT,
    post_content VARCHAR(255)
);

CREATE TABLE user_preferences (
    user_id UUID PRIMARY KEY,
    post_visibility BOOL,
    profile_visibility BOOL
);

CREATE TABLE post_visibility_settings (
    setting_id UUID PRIMARY KEY,
    setting_name VARCHAR(255),
    permission_type VARCHAR(50)
);

CREATE TABLE profile_visibility_settings (
    setting_id UUID PRIMARY KEY,
    setting_name VARCHAR(255),
    permission_type VARCHAR(50)
);

CREATE TABLE users (
 user_id INT PRIMARY KEY,
 username VARCHAR(50) NOT NULL,
 email VARCHAR(100) NOT NULL,
 privacy_settings BOOLEAN
 );

CREATE TABLE friends (
 friendship_id INT PRIMARY KEY,
 user_id1 INT NOT NULL,
 user_id2 INT NOT NULL,
 accepted BOOLEAN
 );

CREATE TABLE users (
 user_id SERIAL PRIMARY KEY,
 username VARCHAR(50) NOT NULL,
 email VARCHAR(100) NOT NULL,
 privacy_level INT
);

CREATE TABLE posts (
 post_id SERIAL PRIMARY KEY,
 user_id INT REFERENCES users(user_id),
 post_content TEXT,
 visibility_level INT
);

CREATE TABLE user_preferences (
 user_id INT REFERENCES users(user_id),
 allow_friends BOOLEAN,
 allow_followers BOOLEAN
);

CREATE TABLE friends (
 user_id1 INT REFERENCES users(user_id),
 user_id2 INT REFERENCES users(user_id),
 friend_status INT
);

CREATE TABLE followers (
 follower_id INT REFERENCES users(user_id),
 user_id INT REFERENCES users(user_id),
 follow_status INT
);
